import React, { Component } from 'react';
import sun from '../images/icon-sun.svg';
import moon from '../images/icon-moon.svg';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isDark: localStorage.getItem('theme')===null||'light'?false:true
        }
        this.toggleTheme = this.toggleTheme.bind(this);
    }

    componentDidMount() {
        console.log('componentDidMount')
        if (!localStorage.getItem('theme')) {
            localStorage.setItem('theme', 'light')
            this.setState((state) => ({
                theme: 'light'
            }))
        } else {
            document.querySelector('html').setAttribute('data-theme', localStorage.getItem('theme'));
            this.setState((state) => ({
                theme: localStorage.getItem('theme')
            }))
        }
    }

    toggleTheme() {
        if (!this.state.isDark) {
            localStorage.setItem('theme', 'dark');
            document.querySelector('html').setAttribute('data-theme', 'dark')
        } else {
            localStorage.setItem('theme', 'light');
            document.querySelector('html').setAttribute('data-theme', 'light')
        }
    }

    render() {
        const { isDark } = this.state;
        const { toggleTheme } = this;
        return (
            <div className='home'>
                <div className='top'>{console.log(this.state.isDark)}</div>
                <div className='bottom'></div>
                <div className='content'>
                    <div className='top-part'>
                        <div className='title'>TODO</div>
                        <div onClick={toggleTheme} className='theme'><img src={moon} alt="theme" /></div>
                    </div>
                    <div className='search-part'>
                        <input type="text" placeholder='Create a new todo...' />
                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
