import React, { Component } from 'react';
import sun from '../images/icon-sun.svg';
import moon from '../images/icon-moon.svg';

class Home extends Component {
    constructor(props) {
        super();
        this.state = {
            theme : props.theme===null?'light':props.theme
        }
        this.toggleTheme = this.toggleTheme.bind(this)
    }

    toggleTheme(paramTheme) {
        let isDark = paramTheme==='dark'?false:true;
        if (localStorage.getItem('theme')==='light') {
            localStorage.setItem('theme', 'dark');
            document.querySelector('html').setAttribute('data-theme', 'dark')
            this.setState((state)=>({
                theme: 'dark'
            }))
        } else {
            localStorage.setItem('theme', 'light');
            document.querySelector('html').setAttribute('data-theme', 'light')
            this.setState((state)=>({
                theme: 'light'
            }))
        }
    }

    render() {
        const { toggleTheme } = this;
        const { theme } = this.state;
        return (
            <div className='home'>
                <div className='top'></div>
                <div className='bottom'></div>
                <div className='content'>
                    <div className='top-part'>
                        <div className='title'>TODO</div>
                        <div onClick={()=>toggleTheme(localStorage.getItem('theme'))} className='theme'><img src={theme==='light'?moon:sun} alt="theme" /></div>
                    </div>
                    <div className='search-part'>
                        <input type="text" placeholder='Create a new todo...' />
                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
