import React from 'react';
import closeIcon from '../images/icon-cross.svg';

class ResultBody extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: {
                0: {
                    text: 'Asilbek Topilov',
                    active: true
                },
                1: {
                    text: 'Asilbek Topilovvvvv',
                    active: false
                },
                2: {
                    text: 'Asilbek Topilovvvvvvvvvv',
                    active: true
                }
            },
            todosForMap: {},
            filter: 'all'
        };
        this.changeFilter = this.changeFilter.bind(this);
        this.filter = this.filter.bind(this);
    }

    // ComponentDidMount Lifecycle
    componentDidMount() {
        this.filter()
    }

    // Filter Items (All, Active, Completed)
    filter() {
        if (this.state.filter === 'all') {
            this.setState((state) => ({
                todosForMap: state.todos
            }))
        } else if (this.state.filter === 'active') {
            this.setState((state) => ({
                todosForMap: Object.values(state.todos).filter(item => item.active === true)
            }), console.log(this.state.todosForMap))
        } else {
            this.setState((state) => ({
                todosForMap: Object.values(state.todos).filter(item => item.active === false)
            }), console.log(this.state.todosForMap))
        }
    }

    // Change Filter Property of Items in order to Filter
    changeFilter(toWhich) {
        this.setState({
            filter: toWhich
        }, this.filter)
    }

    // Delete Item
    delete(index) {
        delete this.state.todos[index];
        this.setState({
            todos: this.state.todos
        }, this.filter)
    }

    render() {
        const { todos, todosForMap, filter } = this.state;
        return (
            <div className='body'>
                <div className='body-inside'>
                    <div className='items'>
                        {Object.values(todosForMap).map((item, index) => (
                            <div key={index} className='item'>
                                {item.text}
                                <div className='circle'></div>
                                <div onClick={()=>this.delete(Object.keys(todosForMap)[index])} className='close'> <img src={closeIcon} alt="X" /> </div>
                            </div>
                        ))}
                    </div>
                    <div className='body-bottom'>
                        <div className='text'>{todos.length} items left</div>
                        <div className='text tabs'>
                            <div onClick={() => this.changeFilter('all')} className={`tab ${filter === 'all' ? 'active' : ''}`}> All </div>
                            <div onClick={() => this.changeFilter('active')} className={`tab ${filter === 'active' ? 'active' : ''}`}> Active </div>
                            <div onClick={() => this.changeFilter('completed')} className={`tab ${filter === 'completed' ? 'active' : ''}`}> Completed </div>
                        </div>
                        <div className='text clear-completed'>Clear Completed</div>
                    </div>
                </div>
                <div className='body-bottom-mobile'>
                    <div className='text tabs'>
                        <div onClick={() => this.changeFilter('all')} className={`tab ${filter === 'all' ? 'active' : ''}`}> All </div>
                        <div onClick={() => this.changeFilter('active')} className={`tab ${filter === 'active' ? 'active' : ''}`}> Active </div>
                        <div onClick={() => this.changeFilter('completed')} className={`tab ${filter === 'completed' ? 'active' : ''}`}> Completed </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default ResultBody;
