import React from 'react';
import closeIcon from '../images/icon-cross.svg';

class Items extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: {
                0: {
                    active: true,
                    text: 'Asilbek Topilov'
                },
                1: {
                    active: true,
                    text: 'Asilbek Topilovvv'
                },
                2: {
                    active: true,
                    text: 'Asilbek Topilovvvvvvvvv'
                },
                3: {
                    active: true,
                    text: 'Asilbek Topilovvvvvvvvvvvvvvvvv'
                }
            }
        };
    }

    componentDidMount() {
        /* if (localStorage.getItem('todos')) {
            this.setState((state) => ({
                todos: JSON.parse(localStorage.getItem('todos'))
            }))
        } else {
            localStorage.setItem('todos', JSON.stringify({}))
        } */
    }

    render() {
        const { todos } = this.state;
        return (
            <div className='items'>
                {Object.values(todos).forEach((item, index, array) => {
                    <div>
                        {item.text}
                    </div>
                })}
                {/* <div className={`item ${!item.active?'disabled':''}`}>
                        {item.text}
                        <div className='circle'></div>
                        <div className='close'><img src={closeIcon} alt="X" /></div>
                    </div> */}
            </div>
        );
    }
}

export default Items;
