import React from 'react';
import closeIcon from '../images/icon-cross.svg';

class Items extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: {
                0: {
                    active: true,
                    text: 'Asilbek Topilov'
                },
                1: {
                    active: true,
                    text: 'Asilbek Topilovvv'
                },
                2: {
                    active: true,
                    text: 'Asilbek Topilovvvvvvvvv'
                },
                3: {
                    active: true,
                    text: 'Asilbek Topilovvvvvvvvvvvvvvvvv'
                }
            }
        };
    }

    componentDidMount() {
        this.props.get
