import React from 'react';
import Items from './Items';

class ResultBody extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: {},
            filter: 'all'
        };
        this.getTodos = this.getTodos.bind(this)
    }

    getTodos(todosParam) {
        this.setState((state) => ({
            todos: Object.values(todosParam)
        }))
    }

    render() {
        const {todos, filter} = this.state;
        return (
            <div className='body'>
                <div className='body-inside'>
                    <Items  getTodos={this.getTodos}/>
                    <div className='body-bottom'>
                        <div className='text'>{todos.length} items left</div>
                        <div className='text tabs'>
                            <div className={`tab ${filter==='all'?'active':''}`}> All </div>
                            <div className={`tab ${filter==='active'?'active':''}`}> Active </div>
                            <div className={`tab ${filter==='completed'?'active':''}`}> Completed </div>
                        </div>
                        <div className='text clear-completed'>Clear Completed</div>
                    </div>
                    <div className='body-bottom-mobile'>
                        <div className='text tabs'>
                            <div className={`tab ${filter==='all'?'active':''}`}> All </div>
                            <div className={`tab ${filter==='active'?'active':''}`}> Active </div>
                            <div className={`tab ${filter==='completed'?'active':''}`}> Completed </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default ResultBody;
